/**
 * Modulation Report Web Display
 * 
 * Displays the optimized modulation report data generated by 
 * modulation_report_generator.py in a nice web interface

 */

// Configuration
$REPORT_DIR = 'reports';
$DEFAULT_DATE = date('Y-m-d');

// Get requested date from URL parameter
$report_date = $_GET['date'] ?? $DEFAULT_DATE;

// Validate date format
if (!preg_match('/^\d{4}-\d{2}-\d{2}$/', $report_date)) {
    $report_date = $DEFAULT_DATE;
}

// Load report data
$json_file = $REPORT_DIR . "/reports/modulation_report_{$report_date}_web.json";

if (!file_exists($json_file)) {
    die("Report for {$report_date} not found. Please generate the report first.");
}

$report_data = json_decode(file_get_contents($json_file), true);

if (!$report_data) {
    die("Failed to load report data for {$report_date}");
}

